import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class JwtClientService {

  token: any
  constructor(private http:HttpClient ,private aut :AuthSe) { }

  public generateToken(request:any){
    console.log("from jwt",request)
    return this.token=this.http.post("http://localhost:8081/user/login",request,{responseType:'text' as 'json'});

  }

  public welcome(){
    token = this.au
    console.log(token);
    let tokenStr='Bearer '+token;
    console.log(tokenStr)
    const headers=new HttpHeaders().set("Authorization",tokenStr)
    return this.http.get("http://localhost:8081/user/home",{headers,responseType:'text' as 'json'});
  }
}
