import { Component, Input, OnInit } from '@angular/core';
import { JwtClientService } from '../jwt-client.service';
import { AnyCatcher } from 'rxjs/internal/AnyCatcher';
import { ActivatedRoute } from '@angular/router';
import { Location } from '@angular/common';

@Component({
  selector: 'app-edit-task',
  templateUrl: './edit-task.component.html',
  styleUrls: ['./edit-task.component.css']
})
export class EditTaskComponent implements OnInit{

  public task:any;
  constructor(private service:JwtClientService,private route:ActivatedRoute,private location:Location){
    this.task=service.getTask();
    // const resp=service.getTask();
    // resp.subscribe((data:any)=>{
    //   console.log(data);  
    //   this.task=data;
    // })
  }


  ngOnInit(): void {
    console.log("this",this.task)
  }
  updateTask(data:any){
      console.log(data.value.task)
      console.log(this.route.snapshot.params['id'])
      const id=parseInt(this.route.snapshot.params['id']);
      const resp=this.service.updateTask(id,data.value.task);
      resp.subscribe((data)=>{
        console.log(data);
        this.location.back();
      },()=>{
        this.location.back();
      })
  }
  onCancel(){
    this.location.back();
  }
}
<h2 class="mt-3" style="text-align: center;">Update Task</h2>
<hr>
<form class="mt-3" (ngSubmit)="updateTask(ref)" #ref="ngForm" style="width: 50%;">
  <div class="task" ngModelGroup="task" #taskdata="ngModelGroup">
    <div class="form-group">
      <label for="formGroupExampleInput">Name</label>
      <input type="text" class="form-control" id="formGroupExampleInput" name="name" [ngModel]="task.name"  required>
    </div>
    <div class="form-group">
      <label for="formGroupExampleInput2">Start Date</label>
      <input type="text" class="form-control" id="formGroupExampleInput2" name="startDate"   [ngModel]="task.startDate"  required>
    </div>

    <div class="form-group">
      <label for="formGroupExampleInput2">End Date</label>
      <input type="text" class="form-control" id="formGroupExampleInput2" name="endDate"  ngModel [ngModel]="task.endDate" required>
    </div>
    <div class="form-group">
      <label for="formGroupExampleInput2">Status</label>
      <input type="text" class="form-control" id="formGroupExampleInput2" name="status"  ngModel [ngModel]="task.status" required>
    </div>
    <div class="form-group">
      <label for="formGroupExampleInput2">Priority</label>
      <select class="form-control" id="role" required ngModel name="role">
        <option value="">Select your role</option>
        <option value="manager">Manager</option>
        <option value="user">User</option>
    </select>
    </div>
    <div class="form-group">
      <label for="role">Role:</label>
      <select class="form-control" id="role" required ngModel name="role">
          <option value="">Select your role</option>
          <option value="manager">Manager</option>
          <option value="user">User</option>
      </select>
  </div>

    <div class="form-group">
      <label for="formGroupExampleInput2">Progress</label>
      <input type="text" class="form-control" id="formGroupExampleInput2" name="progress"  ngModel [ngModel]="task.progress" required>
    </div>
      <button type="submit" class="btn btn-primary ml-3 mt-3 mb-3 mr-3" >Update</button>
      <button type="button" class="btn btn-danger   mx-3" (click)="onCancel()">Cancel</button>
  </div>
  </form>
